const shortUrlEl=document.getElementById('short-url');const copyButtonEl=document.getElementById('copy-button');copyButtonEl.addEventListener('click',()=>{copyDivToClipboard(shortUrlEl);copyButtonEl.classList.add('copied');setTimeout(()=>{copyButtonEl.classList.remove('copied')},500)});function copyDivToClipboard(node){const range=document.createRange();range.selectNode(node);window.getSelection().removeAllRanges();window.getSelection().addRange(range);document.execCommand("copy");window.getSelection().removeAllRanges()}